if (locationData != null){
  	this.view.MainMap.zoomLevel = 15;
	this.view.MainMap.navigateToLocation(locationData, false, false);

  	currentLocationPin = {
      id : "currentPositionPin", // id is mandatory for every pin
      lat : locationData["lat"],
      lon : locationData["lon"],
      name : "Current Position",
      image : "arrow_left.png",
      focusImage:"arrow_left.png",  //focus image will be shown while map pin selected
      desc: "You are here!",
      showCallout : false,
      meta: {
          color: "none",
          label: ""
      }
	};

  	this.view.MainMap.addPin(currentLocationPin);
}
